let burger=document.getElementById("menu");function ibg(){let e=document.querySelectorAll(".ibg");for(var t=0;t<e.length;t++)e[t].querySelector("img")&&(e[t].style.backgroundImage="url("+e[t].querySelector("img").getAttribute("src")+")")}burger.onclick=function(){let e=burger.classList,t=document.getElementById("menu__body");"_active"!=e[e.length-1]?(t.classList.add("_active"),e.add("_active")):(t.classList.remove("_active"),e.remove("_active"))},ibg();